mapper.select:
select * from ( select rownum as rn, a.* from ( select CI.csno, CI.cname, CI.clocal, CI.image, NVL(CR.month,0), NVL(CR.year,0), NVL(CR.total,0), ( select count(*) as cnt from clubmember where cmLevel != 'ZA' and cmLevel != 'XA' and cmLevel != 'QA' and cmcsno = CI.csno ) members, CI.description from club_info CI , club_rank CR where CI.csno = CR.csno(+)
and CI.cname = #{srchValue}
and CI.clocal = #{srchValue}
order by
CI.clocal
CR.total DESC
CI.cname
CI.cname
) a ) where rownum <= #{pageSize} and rn > (#{pageNum}-1)*#{pageSize}

select * from ( select rownum as rn, a.* from ( select CI.csno, CI.cname, CI.clocal, CI.image, CR.month, CR.year, CR.total, ( select count(*) as cnt from clubmember where cmLevel != 'ZA' and cmLevel != 'XA' and cmLevel != 'QA' and cmcsno = CI.csno ) members, CI.description from club_info CI , club_rank CR , ( select count(*) as cnt from clubmember where cmLevel != 'ZA' and cmLevel != 'XA' and cmLevel != 'QA' and cmcsno = CI.csno ) members where CI.csno = CR.csno
and CI.cname = #{srchValue}
and CI.clocal = #{srchValue}
order by
CI.clocal
CR.total
CI.clocal
) a ) where rownum <= #{pageSize} and rn > (#{pageNum}-1)*#{pageSize}

mapper.insert:
insert into club_info(
	csno	,cname	,clocal,
	ctime	,cip	,cmaker,
	cmakername, description, image
) values (
	csno_seq.nextval	,#{cname,jdbcType=VARCHAR}	,#{clocal,jdbcType=VARCHAR},
	to_char(sysdate, 'yyyymmddhh24miss')	,#{cip,jdbcType=VARCHAR} ,#{cmaker,jdbcType=NUMERIC},
	#{cmakername,jdbcType=VARCHAR}, #{description,jdbcType=VARCHAR}, #{image,jdbcType=VARCHAR}
)
